import { heroes } from './data/heroes';

const getHeroeById = (heroeId) => {
    return heroes.find((heroe) => {
        if (heroe.id == heroeId) {
            return heroe;
        } else {
            return {
                message: 'Heroe no encontrado'
            }
        }
    });
}

console.log('Heroe con id 2 -> ', getHeroeById(2));
console.log('Heroe con id 3 -> ', getHeroeById(3));
