const persona = {
    nombre: 'Tony',
    edad: 45,
    clave: 'Iron Man'
};
const { nombre, edad, clave } = persona;
console.log(nombre + ' , ' + edad + ' , ' + clave);
console.log('-------------------------------------');

const useContext = ({ nombre, edad, clave }) => {
    // console.log(nombre + ' , ' + edad + ' , ' + clave);

    return {
        nombreClave: clave,
        anios: edad
    }
}

const avenger = useContext(persona);
console.log(avenger);
